SELECT t2.doc_code AS bom_code, t0.material_code as production_material_code, t3.material_code, t3.unit, t3.specifications, t3.qty AS standard_qty FROM sal_order_detail t0 INNER JOIN sal_order t1 ON t1.id = t0.pid INNER JOIN yujiakeji_bom t2 ON t2.material_code = t0.material_code INNER JOIN yujiakeji_bom_detail t3 ON t3.pid = t2.id WHERE t1.del_flag = 0 AND t1.audit = 1 AND t2.audit = 1 AND t2.bom_type = 1 AND t2.del_flag = 0 AND t0.id IN
#{id}
GROUP BY t3.id
